    ALL_PRODUCTS.forEach(item=> {
        if(cartData.includes(item['_id'])){
            totalCartValue+=Number(item.price)
        }
    })

    return (
        <div>
{
ALL_PRODUCTS.forEach(item=> {
        (cartData.includes(item['_id']))?(
<p><b>{item.title}</b></p>
        ):null
    })
}

       <p style={{width:'100%',color:'green'}} >
          TOTAL CART VALUE IS : {totalCartValue} 
       </p>
       </div>
    )

}
